import React, {useContext, useState, useEffect} from 'react'
import {GlobalState} from '../../../GlobalState'
import axios from 'axios'

function Wlist() {
    const state = useContext(GlobalState)
    //ws
    const [wishlist, setWishlist] = state.userAPI.wishlist
    const [token] = state.token
    const [total, setTotal] = useState(0)
    const [isCheck,SetIsCheck] = useState(false)

    //add to wishlist
    const addToWishlist = async (cart) => {
        //
        await axios.patch('/user/addwishlist', {wishlist}, {
            headers: {Authorization: token}
        })
    }
    //
    const removeProduct = id =>{
        if(window.confirm("Remove from your wish list?")){
            wishlist.forEach((item, index) => {
                if(item._id === id){
                    cart.splice(index, 1)
                }
            })

            setCart([...cart])
            addToWishlist(cart)
        }

        if(cart.length === 0) 
        return <h2 style={{textAlign: "center", fontSize: "5rem"}}>Wishlist Empty</h2> 

         return (
        <div>
            {
                cart.map(product => (
                    <div className="detail cart" key={product._id}>
                        <img src={product.images.url} alt="" />

                        <div className="box-detail">
                            <h2>{product.title}</h2>

                            <h3>$ {product.price * product.quantity}</h3>
                            <p>{product.description}</p>
                            <p>{product.content}</p>

                            <div className="amount">

                                <span>{product.quantity}</span>

                            </div>

                            <div className="delete" 
                            onClick={() => removeProduct(product._id)}>
                                X
                            </div>
                        </div>
                    </div>
                ))
            }

            <div className="total">
                <h3>Total: $ {total}</h3>

            </div>
        </div>
    )
    }
}

export default Wlist
